mapper.select:
SELECT resume_num, recruit_title, name, apply_date, resume_title, career, addr, doc_pass_flag, final_pass_flag FROM ( SELECT ROW_NUMBER() OVER (ORDER BY apply.apply_date ASC) AS rnum, recruit.title AS recruit_title, resume.resume_num, users.name, apply.apply_date, resume.title AS resume_title, CASE WHEN c.total_days IS NULL THEN '신입' ELSE '경력(' || CASE WHEN FLOOR(c.total_days / 365) > 0 THEN FLOOR(c.total_days / 365) || '년' ELSE '' END || CASE WHEN FLOOR(c.total_days / 365) > 0 AND FLOOR(MOD(c.total_days, 365) / 30) > 0 THEN ' ' ELSE '' END || CASE WHEN FLOOR(MOD(c.total_days, 365) / 30) > 0 THEN FLOOR(MOD(c.total_days, 365) / 30) || '개월' WHEN c.total_days > 0 AND FLOOR(MOD(c.total_days, 365) / 30) = 0 THEN CASE WHEN FLOOR(c.total_days / 365) = 0 THEN '1개월 미만' ELSE '' END ELSE '' END || ')' END AS career, resume.addr, CASE WHEN apply.progress_state = '지원완료' THEN '미정' WHEN apply.progress_state = '불합격' THEN '불합격' ELSE '합격' END AS doc_pass_flag, CASE WHEN apply.progress_state = '불합격' THEN '불합격' WHEN apply.progress_state = '최종합격' THEN '합격' ELSE '미정' END AS final_pass_flag FROM apply LEFT JOIN resume ON apply.resume_num = resume.resume_num LEFT JOIN recruit ON recruit.recruit_num = apply.recruit_num LEFT JOIN users ON resume.user_id = users.user_id LEFT JOIN education ON resume.resume_num = education.resume_num LEFT JOIN ( SELECT resume_num, SUM( CASE WHEN resignation_date IS NULL THEN TRUNC(CURRENT_DATE) - join_date ELSE resignation_date - join_date END ) AS total_days FROM career GROUP BY resume_num ) c ON apply.resume_num = c.resume_num
AND users.name LIKE '%' || #{keyword} || '%'
AND resume.title LIKE '%' || #{keyword} || '%'
AND resume.self_introduction LIKE '%' || #{keyword} || '%'
AND resume.addr LIKE '%' || #{keyword} || '%'
AND apply.apply_date >= #{startDate}
AND apply.apply_date <= #{endDate}
AND ( CASE WHEN c.total_days IS NULL THEN '신입' ELSE '경력' END ) = #{career}
AND apply.recruit_num = #{recruitId}
AND education.school_classification >= #{endSchool}
) WHERE rnum BETWEEN #{startNum} AND #{endNum}

SELECT JSON_OBJECT(
    'skills' VALUE JSON_ARRAYAGG(
        JSON_OBJECT(
            'skill_name' VALUE sl.skill_name
        )
    )
) AS skills_json
FROM resume r
JOIN skill s ON s.resume_num = r.resume_num
JOIN skill_list sl ON s.skill_code = sl.skill_code
WHERE r.resume_num = #{resumeNum}

    SELECT JSON_OBJECT(
	  'certifications' VALUE JSON_ARRAYAGG(
	    JSON_OBJECT(
	      'certificate_name' VALUE cert.certificate_name,
	      'publisher' VALUE cert.publisher,
	      'acquisition_date' VALUE TO_CHAR(cert.acquisition_date, 'YYYY-MM-DD')
	    )
	  )
	) AS certifications_json
	FROM resume r
	JOIN certificate cert ON cert.resume_num = r.resume_num
	WHERE r.resume_num = #{resumeNum}

SELECT JSON_OBJECT(
  'languages' VALUE JSON_ARRAYAGG(
    JSON_OBJECT(
      'language' VALUE lang.language,
      'test_name' VALUE lang.test_name,
      'lang_level' VALUE lang.lang_level,
      'category' VALUE lang.category
    )
  )
) AS languages_json
FROM resume r
JOIN language lang ON lang.resume_num = r.resume_num
WHERE r.resume_num = #{resumeNum}
